import React, {Component} from 'react'
import {Link} from 'react-router-dom'
import queryString from 'query-string'

export default class PageRange extends Component {
    constructor(props) {
        super(props);
        this.state = {
            max: props.max,
            baseUrl: props.baseUrl,
        }
    }

    render() {
        const range = [];
        for (let i=1; i<=this.state.max; i++) {
            range.push(i)
        }
        const getParams = queryString.parse(window.location.search);
        return (

        <div className="Pagination">
            <p>
                {range.map((i) => {
                    getParams.page = i;
                    return <nav aria-label="..."><ul class="pagination pagination-lg"><Link to={{pathname: this.state.baseUrl + '?' + queryString.stringify(getParams)}}>{i}</Link></ul></nav>
                })}
            </p>
        </div>
        );
    }

}
