import React, { Component, PureComponent } from 'react';
import './App.css';


class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      datas: []
    };
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res => res.json())
    .then(data => {
      let usersListe = data.map((datas) =>{return (
        <li key={datas.id} onClick={this.onNameClicked.bind(this, datas.name)}>{datas.name}</li>
      )})
      this.setState({usersListe: usersListe})
      console.log(this.state.usersListe)
    })
  }

  onNameClicked(param){

  }

  render(){
    return (
      <div className="App">
        <div>
          <h1>Users List</h1>
          {this.state.usersListe}
        </div>
      </div>
    );
  }
}

class ListPost extends PureComponent{

  constructor(props){
    super(props);
    this.state = {
      datas: []
    };
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/posts')
    .then(res => res.json())
    .then(userPosts => {
      let postsList = data.map((userPosts) =>{return (
        <li key={userPosts.id} onClick={this.onNameClicked.bind(this, datas.name)}>{datas.name}</li>
      )})
      this.setState({postsList: postsList})
      console.log(this.state.usersListe)
    })
  }

  render(){
    return(
      <div></div>
    )
  }
}

export default App;
