import React, { Component } from 'react';
import './App.css';
import Posts from './Posts.js'

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      usersListe: []
    };
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res => res.json())
    .then(data => {
      this.setState({usersListe: data})
      console.log(this.state.usersListe)
    })
  }

  onNameClicked(param){
    Posts(1)
  }

  render(){
    return (
      <div className="App">
        <div>
          <h1>Users List</h1>
          {this.state.usersListe.map((usersListe) =>{return (
            <h2 key={usersListe.id} onClick={this.onNameClicked.bind(this, usersListe.name)}>{usersListe.name}</h2>
          )})}
        </div>
      </div>
    );
  }
}

export default App;
