import React, { Component } from 'react';
import './App.css';

class Posts extends Component {

  constructor(props){
    super(props);
    this.state = {
      usersPosts: []
    };
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/posts')
    .then(res => res.json())
    .then(data => {
      this.setState({usersPosts: data})
      console.log(this.state.usersPosts)
    })
  }

  render(){
    const ListPosts = this.state.usersPosts
    return (
      <div className="App">
        <div>
          <App ListPosts={this.state.usersPosts} />
        </div>
      </div>
    );
  }
}

export default Posts;
